import openpyxlimport shutilfrom datetime import datetimeimport osimport refrom openpyxl.styles import Color, PatternFill, Font, Borderfrom openpyxl.styles import colorsfrom openpyxl.cell import CellMaster_workbook = openpyxl.load_workbook('C:\Test\Dates.xlsx')Master_Compare_sheet = Master_workbook ['Sheet1']Validation_Rows = Master_Compare_sheet.max_rowfor i in range(1,Validation_Rows):    Baseline_File = open('C:\Test\Basefile.txt', "a")    STARTDATE = Master_Compare_sheet.cell(row =i,column = 1).value    ENDDATE = Master_Compare_sheet.cell(row =i,column = 2).value    PERIOD = Master_Compare_sheet.cell(row =i,column = 3).value    #Baseline_File.write(Line1)    Baseline_File.write("<insert schemaName=?${mirevSchemaName}? tableName=?MI_EV_PERIOD?>\n")    Baseline_File.write	("  <column name=?ID? valueComputed=?SEQ_ENTITY_VARIABLE_PERIOD_ID.nextval? />\n")    Baseline_File.write	("  <column name=?FREQUENCY? value=?Monthly? />\n")    Baseline_File.write	("  <column name=?START_DATE? value=?"+STARTDATE+"? />\n")    Baseline_File.write	("  <column name=?END_DATE? value=?"+ENDDATE+"? />\n")    Baseline_File.write	("  <column name=?SEQUENCE? value=?"+PERIOD+"? />\n")    Baseline_File.write	("  <column name=?DISC_ID? value=?1? />\n")    Baseline_File.write	("  <column name=?INSERTED_USER? />\n")    Baseline_File.write	("  <column name=?INSERTED_DATE? />\n")    Baseline_File.write	("  <column name=?LAST_UPDATED_USER? />\n")    Baseline_File.write	("  <column name=?LAST_UPDATED_DATE? />\n")    Baseline_File.write("</insert>\n")    Baseline_File.close()